import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function ConversationsPage() {
  const location = useLocation();
  const clientDetails = location.state;
  console.log(clientDetails);

  const [historyItem, setHistoryItem] = useState(null);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    axios
      .post('/viewhistory', { clientDetails })
      .then(response => {
        setHistoryItem(response.data);
      })
      .catch(error => {
        console.error('Error fetching history:', error);
      });
  };

  return (
    <div>
      <h2>History Item:</h2>
      {historyItem ? (
        <div>
          {historyItem.clientDetails && (
            <p>Client Details: {historyItem.clientDetails.field}</p>
          )}
          {historyItem.conversationContent && (
            <p>Conversation Content: {historyItem.conversationContent.join(', ')}</p>
          )}
          {historyItem.displayedJobs && (
            <p>Displayed Jobs: {historyItem.displayedJobs.join(', ')}</p>
          )}
          {historyItem.selectedJobs && (
            <p>Selected Jobs: {historyItem.selectedJobs.join(', ')}</p>
          )}
          {historyItem.experienceEducation && (
            <p>Experience & Education: {historyItem.experienceEducation}</p>
          )}
          {historyItem.feedbackOnTermination && (
            <p>Feedback on Termination: {historyItem.feedbackOnTermination}</p>
          )}
          {historyItem.selectedFeatures && (
            <p>Date: {historyItem.selectedFeatures.date}</p>
          )}
        </div>
      ) : (
        <p>No history item found.</p>
      )}
    </div>
  );
}

export default ConversationsPage;
