import React, { useState } from 'react';

import chatFlow from '../chatBotLogic/convert_tree_to_json.js';
import './EditableList.css';


const TreeNode = ({ node }) => {
  const [isExpanded, setExpanded] = useState(false);
    const [items, setItems] = useState(chatFlow)

  const handleEdit = (text) => {
    setItems(
      items.map((item) => {
          return { ...item, text };
      })
    );
  };

  return (
    <ul className="editable-list">
      <li>
        {node.title+": "}
        {node.text}
        <br/>
        {node.children && node.children.length > 0 && (
          <button className="add-button" onClick={() => setExpanded(!isExpanded)}>
            {isExpanded ? 'Collapse' : 'Expand'}
          </button>
          // <input
          //     type="text"
          //     value={node.text}
          //     onChange={(e) => handleEdit(node.texte.target.value)}
          // />
        )}
      </li>
      {isExpanded &&
        node.children.map((childNode, index) => (
          <TreeNode key={index} node={childNode} />
        ))}
    </ul>
  );
};


const Tree = ({ data }) => {
  return <TreeNode node={data} />;
};

const ViewChatFlow = () => {
  return (
    <div>
      <Tree data={chatFlow} />
    </div>
  );
};

export default ViewChatFlow;

