import React, { useState } from 'react';
// import { flowTry } from 'react-native-svg';

import chatFlow from '../chatBotLogic/convert_tree_to_json.js';
import './EditableList.css';


const TreeNode = ({ nod }) => {
  const [isExpanded, setExpanded] = useState(false);
    const [node, setNode] = useState(nod.text)

  const handleEdit = (text) => {
    setNode({...node, text:{text}});
  }

  return (
    <ul className="editable-list">
      <li>
        {node+": "}<br/>
        {/* {node.text} */}
        <input
        className="textContent"
        type="text"
        value={node}
        onChange={(e) => handleEdit(node, e.target.value)}/>
        <br/>
        {node.children && node.children.length > 0 && (
          <>
              <button className="add-button" onClick={() => setExpanded(!isExpanded)}>
              {isExpanded ? 'Collapse' : 'Expand'}
              </button>
          </>
        )}
        {/* <button className="edit-button">Edit</button> */}
      </li>
      {isExpanded &&
        node.children.map((childNode, index) => (
          <>
          {setNode(childNode)}
          <TreeNode key={index} nod={childNode} />
          </>

        ))}
    </ul>
  );
};


const Tree = ({ data }) => {
  return (
  <>
  <h2 className="title">Editable Chat flow</h2>
  {/* <SvgUri
    width="100%"
    height="100%"
    uri="./flowTry.svg"
  /> */}
    <div className="App">
      <img src="JOBOT/React_JobBot/src/pages/adminArea/flowTry.svg" alt="Your SVG" />
    </div>
  <TreeNode nod={data} />;
  </>
  )
};

const ViewChatFlow = () => {
  return (
    <div>
      <Tree data={chatFlow} />
    </div>
  );
};

export default ViewChatFlow;

