import React from "react";
import {useState,useEffect} from "react";

import "./Options.css";

const FieldOptions = (props) => {
  const [options, setOptions] = useState([]);

  useEffect(()=>{setOptions(props.node.getNextResponse().children[0].options)},[]);//maybe props.node_if_options>0

  // function whichHandler(opt) {
  //   switch(opt) {
  //     case "Other":
  //       return (() => {props.actionProvider.handleOtherField(props.node,opt);});
  //     default:
  //       return (() => {props.actionProvider.handleField(props.node,opt);});
  //   }
  // }

  // var i=1;
  // const buttonsMarkup = options.map((opt) =>{
  //   return (        
  //     <button key={i++} onClick={whichHandler(opt)} className="option-button">
  //     {opt}
  //     </button>);
  // },[]);

  // return <div className="options-container">{buttonsMarkup}</div>;
  //$$$$$$$$$$$$$$$
  // return (
  //   <div>
  //     <input type="radio" value="Male" name="gender" defaultValue={"Male"}/> Male      
  //   </div>
  //   );

    const [selectedOptions, setSelectedOptions] = useState([]);
    const [submitted,setSubmitted]=useState(false);
    
    const handleOptionChange = (event) => {
      const option = event.target.value;
      if (!selectedOptions.includes(option)) {
        setSelectedOptions([option]);
      }
    };
  
    const handleSubmit = (event) => {
      event.preventDefault();
      console.log("Selected Options radio: ", selectedOptions);
      // handle submission logic
      setSubmitted(true);
      switch(selectedOptions[0]) {
        case "Other":
          props.actionProvider.handleOtherField(props.node,selectedOptions[0]);
          break;
        default:
          props.actionProvider.handleField(props.node,selectedOptions[0]);
      }
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <label>
          {["options"].map((opt,index) =>{
            return(
            <label key={index}>
              <br />
              <input
              type="radio"
              value={opt}
              name="field"
              defaultValue={"Engineering"}
              onChange={handleOptionChange} />
              {opt}
            </label>);
          },[])
          }
        </label>
        <br />
        <button type="submit" className="option-button" disabled={submitted}>Submit</button>
      </form>);
};

export default FieldOptions;
