import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function ConversationsPage() {
  const location = useLocation();
  //const { state: { clientDetails } } = location;
  const clientDetails = location.state;
  console.log(clientDetails);


  const [conversations, setConversations] = useState([]);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    // Make an axios post request to fetch history based on username and password
    axios
      .post('/viewhistory', { username: clientDetails.userName, password: clientDetails.password })
      .then(response => {
        setConversations(response.data);
      })
      .catch(error => {
        console.error('Error fetching history:', error);
      });
  };

  return (
    <div>
      <h2>Conversations:</h2>
      {conversations.map(conversation => (
        <div key={conversation.id}>
          <p>{conversation.text}</p>
        </div>
      ))}
    </div>
  );
}

export default ConversationsPage;
