import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function ClientHistory() {
  const location = useLocation();
  const clientDetails = location.state;
  console.log(clientDetails);

  const [conversations, setConversations] = useState([]);
  const [selectedJobs, setSelectedJobs] = useState([]);

  const fetchConversations = () => {
    // Make an axios post request to fetch conversations
    axios.post('/conversations', { clientId: clientDetails.id })
      .then(response => {
        setConversations(response.data);
      })
      .catch(error => {
        console.error('Error fetching conversations:', error);
      });
  };

  const fetchSelectedJobs = () => {
    // Make an axios post request to fetch selected/offered jobs
    axios.post('/selectedJobs', { clientId: clientDetails.id })
      .then(response => {
        setSelectedJobs(response.data);
      })
      .catch(error => {
        console.error('Error fetching selected jobs:', error);
      });
  };

  return (
    <div>
      <h1>History By Fields</h1>
      <nav>
        Tabs of:
        <ul>
          <li>
            <p onClick={fetchConversations}>Conversations</p>
          </li>
          <li>
            <p onClick={fetchSelectedJobs}>Selected/Offered jobs</p>
          </li>
        </ul>
      </nav>

      {/* Render conversations */}
      <div>
        <h2>Conversations:</h2>
        {conversations.map(conversation => (
          <div key={conversation.id}>
            <p>{conversation.text}</p>
          </div>
        ))}
      </div>

      {/* Render selected/offered jobs */}
      <div>
        <h2>Selected/Offered Jobs:</h2>
        {selectedJobs.map(job => (
          <div key={job.id}>
            <p>{job.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ClientHistory;
