import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import './OfferedJobs.css';

function OfferedJobs() {
  const location = useLocation();
  const clientDetails = location.state;
  console.log(clientDetails);

  const [jobItem, setjobItem] = useState(null);

  useEffect(() => {
    fetchjobs();
  }, []);

  const fetchjobs = () => {
    axios
      .post('/offeredjobs', { clientDetails })
      .then(response => {
        setjobItem(response.data);
      })
      .catch(error => {
        console.error('Error fetching history:', error);
      });
  };

  return (
    <div className="offered-jobs">
      <h2>Offered Jobs:</h2>
      {jobItem ? (
        <pre className="job-item">{JSON.stringify(jobItem, null, 2)}</pre>
      ) : (
        <p>No offered jobs found.</p>
      )}
    </div>
  );
}

export default OfferedJobs;
